using System;
using System.Collections.Generic;
using System.IO;
using System.Threading.Tasks;
using ActionMachine.Runtime;
using ActionMachine.Runtime.Controller;
using ActionMachine.Runtime.Helper;
using KinematicCharacterController;
using LocalModels;
using UnityEditor;
using UnityEngine;
using UnityEngine.Rendering;
using Object = UnityEngine.Object;

namespace Runtime
{
    public class TableLoad : ITableLoader
    {
        public Task<byte[]> LoadBytesAsync(string fileName)
        {
            byte[] result = null;
            try
            {
                result = Resources.Load<TextAsset>($"LocalModel/{fileName}").bytes;
            }
            catch (Exception e)
            {
                throw new Exception($"Load {fileName} failed", e);
            }

            return Task.FromResult(result);
        }
    }

    [System.Serializable]
    public class ActionControllerWrapper
    {
        public int CharacterID;
        public ActionCharacterController characterController;
    }

    public class GameManager : MonoBehaviour
    {
        [SerializeField] protected List<ActionControllerWrapper> controllers;


        protected float logicTimer = 0f;
        protected const float logicDeltaTime = 1 / 30f;
        public LocalModelManager _localModelManager;

        public static GameManager Instance;

        private void Start()
        {
            KinematicCharacterSystem.EnsureCreation();
            KinematicCharacterSystem.Settings.AutoSimulation = false;
            KinematicCharacterSystem.Settings.Interpolate = true;


            ActionAssetsHelper.SetLoadEntityAsync(LoadEntityAsync);
            ActionAssetsHelper.SetLoadAssetAsync(LoadAssetAsync);
            Instance = this;
            _localModelManager = new LocalModelManager();
            _localModelManager.InitialiseLocalModels();
            _localModelManager.SetTableLoader(new TableLoad());
            _localModelManager.LoadAll(null, OnLoadTableCallback);
        }

        private async Task<Object> LoadAssetAsync(string arg)
        {
            var request = Resources.LoadAsync<UnityEngine.Object>(TempAssets(arg));
            await request;
            return request.asset;
        }

        private async Task<GameObject> LoadEntityAsync(string arg)
        {
            var request = Resources.LoadAsync<GameObject>(TempAssets(arg));
            await request;
            return request.asset as GameObject;
        }

        private void OnLoadTableCallback()
        {
            _localModelManager.GetTbActionDataInstance().DataList.ForEach(item => Debug.Log(item.Id));
            foreach (var kPair in controllers)
            {
                var data = InitializeCharacterActionData(kPair.CharacterID);
                kPair.characterController.Initialized(data);
            }
        }

        private void Update()
        {
            InputData.Update();
            LogicUpdate();
        }

        private void LogicUpdate()
        {
            logicTimer += Time.deltaTime;
            if (logicTimer >= logicDeltaTime)
            {
                logicTimer -= logicDeltaTime;
                RunLogicUpdate(logicDeltaTime);
            }
        }

        private void RunLogicUpdate(float logicDeltaTime)
        {
            KinematicCharacterSystem.PreSimulationInterpolationUpdate(logicDeltaTime);

            /*
            foreach (var item in controllers)
            {
                item.characterController.LogicUpdate(logicDeltaTime);
            }
            */

            KinematicCharacterSystem.Simulate(logicDeltaTime, KinematicCharacterSystem.CharacterMotors,
                KinematicCharacterSystem.PhysicsMovers);
            KinematicCharacterSystem.PostSimulationInterpolationUpdate(logicDeltaTime);

            //Physics.Simulate(logicDeltaTime);
            InputData.Clear();
        }


        private ActionMachineInitializeData InitializeCharacterActionData(int characterId)
        {
            var characterData = _localModelManager.GetCharacterDataItem(characterId);
            var result = new ActionMachineInitializeData
            {
                InitialStateName = characterData.FirstAction,
                StateActions = new Dictionary<string, ActionConfig>()
            };
            foreach (var action in characterData.Actions)
            {
                var actionData = _localModelManager.GetActionDataItem(action);
                result.StateActions.Add(actionData.Id, new ActionConfig()
                {
                    EnableLoop = actionData.Loop,
                    NextActionId = actionData.NextActionId,
                    Json = LoadActionConfig(actionData.AssetsPath),
                    SerialID = actionData.Id
                });
            }

            result.GlobalActions = new List<ActionConfig>();
            foreach (var action in characterData.GlobalActions)
            {
                var actionData = _localModelManager.GetActionDataItem(action);
                result.GlobalActions.Add(new ActionConfig()
                {
                    EnableLoop = actionData.Loop,
                    NextActionId = null,
                    Json = LoadActionConfig(actionData.AssetsPath),
                    SerialID = actionData.Id
                });
            }

            return result;
        }

        private string LoadActionConfig(string path)
        {
            Debug.LogError($"LoadActionConfig {path}");
            return AssetDatabase.LoadAssetAtPath<TextAsset>(path + ".json").text;
        }

        private string TempAssets(string assets)
        {
            //只取文件名
            //从路径去除文件名
            return Path.GetFileNameWithoutExtension(assets);
        }
    }
}
