using System.Collections.Generic;
using System.IO;
using System.Linq;
using UnityEditor;
using UnityEngine;

public static class FbxMaterialSetup
{
    public static void SetupFbxMaterials()
    {
        string fbxPath = "Assets/_Game/_Example/Arts/2B/2B.fbx";
        string texturePath = "Assets/_Game/_Example/Arts/2B/tex";
        string materialPath = "Assets/_Game/_Example/Arts/2B/Materials";

        // 确保材质文件夹存在
        if (!Directory.Exists(materialPath))
        {
            Directory.CreateDirectory(materialPath);
            AssetDatabase.Refresh();
        }

        // 获取FBX模型
        GameObject fbx = AssetDatabase.LoadAssetAtPath<GameObject>(fbxPath);
        if (fbx == null)
        {
            Debug.LogError($"Failed to load FBX at {fbxPath}");
            return;
        }

        // 获取FBX的所有材质
        ModelImporter importer = AssetImporter.GetAtPath(fbxPath) as ModelImporter;
        if (importer == null)
        {
            Debug.LogError($"Failed to get ModelImporter for {fbxPath}");
            return;
        }

        // 获取所有贴图文件
        string[] textureGuids = AssetDatabase.FindAssets("t:Texture2D", new[] { texturePath });
        Dictionary<string, Texture2D> textureDict = new Dictionary<string, Texture2D>();

        foreach (string guid in textureGuids)
        {
            string path = AssetDatabase.GUIDToAssetPath(guid);
            Texture2D texture = AssetDatabase.LoadAssetAtPath<Texture2D>(path);
            if (texture != null)
            {
                string textureName = Path.GetFileNameWithoutExtension(path).ToLower();
                textureDict[textureName] = texture;
            }
        }

        Debug.Log($"Found {textureDict.Count} textures in {texturePath}");

        // 获取所有子网格的渲染器
        Renderer[] renderers = fbx.GetComponentsInChildren<Renderer>(true);
        HashSet<string> materialNames = new HashSet<string>();

        foreach (var renderer in renderers)
        {
            foreach (var mat in renderer.sharedMaterials)
            {
                if (mat != null)
                {
                    materialNames.Add(mat.name);
                }
            }
        }

        Debug.Log($"Found {materialNames.Count} materials in FBX: {string.Join(", ", materialNames)}");

        // 为每个材质创建新的材质球并分配贴图
        foreach (string matName in materialNames)
        {
            string cleanMatName = matName.Replace(" (Instance)", "").Trim();
            string newMatPath = Path.Combine(materialPath, cleanMatName + ".mat");

            Material material = AssetDatabase.LoadAssetAtPath<Material>(newMatPath);
            if (material == null)
            {
                // 创建新材质 (使用Standard shader)
                material = new Material(Shader.Find("Standard"));
                material.name = cleanMatName;
                AssetDatabase.CreateAsset(material, newMatPath);
                Debug.Log($"Created material: {newMatPath}");
            }

            // 尝试自动分配贴图
            // 查找包含材质名的贴图
            var matchingTextures = textureDict.Where(kvp =>
                kvp.Key.Contains(cleanMatName.ToLower()) ||
                cleanMatName.ToLower().Contains(kvp.Key)
            ).ToList();

            // 如果没有找到匹配的，尝试分配任意贴图（按字母顺序）
            if (matchingTextures.Count == 0)
            {
                matchingTextures = textureDict.Take(1).ToList();
            }

            if (matchingTextures.Count > 0)
            {
                var firstTexture = matchingTextures[0].Value;
                material.SetTexture("_MainTex", firstTexture);
                material.SetColor("_Color", Color.white);
                Debug.Log($"Assigned texture {matchingTextures[0].Key} to material {cleanMatName}");
            }

            EditorUtility.SetDirty(material);
        }

        AssetDatabase.SaveAssets();
        AssetDatabase.Refresh();

        // 现在应用材质到FBX
        foreach (string matName in materialNames)
        {
            string cleanMatName = matName.Replace(" (Instance)", "").Trim();
            string newMatPath = Path.Combine(materialPath, cleanMatName + ".mat");
            Material material = AssetDatabase.LoadAssetAtPath<Material>(newMatPath);

            if (material != null)
            {
                // 使用FBX的材质重映射
                importer.SearchAndRemapMaterials(ModelImporterMaterialName.BasedOnMaterialName,
                    ModelImporterMaterialSearch.Local);

                // 如果需要，可以通过脚本直接替换材质
                EditorUtility.SetDirty(importer);
            }
        }

        importer.SaveAndReimport();

        Debug.Log($"FBX material setup complete! Created {materialNames.Count} materials in {materialPath}");
    }
}

