using System.Collections.Generic;
using System.IO;
using System.Linq;
using UnityEditor;
using UnityEngine;

public static class FbxMaterialSetup
{
    public static void SetupFbxMaterialsUrp()
    {
        string fbxPath = "Assets/_Game/_Example/Arts/2B/2B.fbx";
        string texturePath = "Assets/_Game/_Example/Arts/2B/tex";
        string materialPath = "Assets/_Game/_Example/Arts/2B/Materials";

        // 确保材质文件夹存在
        if (!Directory.Exists(materialPath))
        {
            Directory.CreateDirectory(materialPath);
            AssetDatabase.Refresh();
        }

        // 获取FBX模型
        GameObject fbx = AssetDatabase.LoadAssetAtPath<GameObject>(fbxPath);
        if (fbx == null)
        {
            Debug.LogError($"Failed to load FBX at {fbxPath}");
            return;
        }

        // 获取所有贴图文件
        string[] textureGuids = AssetDatabase.FindAssets("t:Texture2D", new[] { texturePath });
        List<TextureInfo> textureInfos = new List<TextureInfo>();

        foreach (string guid in textureGuids)
        {
            string path = AssetDatabase.GUIDToAssetPath(guid);
            Texture2D texture = AssetDatabase.LoadAssetAtPath<Texture2D>(path);
            if (texture != null)
            {
                FileInfo fileInfo = new FileInfo(path);
                textureInfos.Add(new TextureInfo
                {
                    texture = texture,
                    name = Path.GetFileNameWithoutExtension(path),
                    path = path,
                    size = fileInfo.Length
                });
            }
        }

        // 按文件大小排序（大的贴图通常是主贴图）
        textureInfos = textureInfos.OrderByDescending(t => t.size).ToList();

        Debug.Log($"Found {textureInfos.Count} textures:");
        foreach (var info in textureInfos)
        {
            Debug.Log($"  {info.name}: {info.size / 1024}KB");
        }

        // 获取所有材质名称
        Renderer[] renderers = fbx.GetComponentsInChildren<Renderer>(true);
        HashSet<string> materialNames = new HashSet<string>();

        foreach (var renderer in renderers)
        {
            foreach (var mat in renderer.sharedMaterials)
            {
                if (mat != null)
                {
                    materialNames.Add(mat.name);
                }
            }
        }

        Debug.Log($"\nFound {materialNames.Count} materials in FBX:");
        foreach (var matName in materialNames)
        {
            Debug.Log($"  {matName}");
        }

        // 智能匹配材质和贴图
        var materialTextureMap = CreateMaterialTextureMapping(materialNames.ToList(), textureInfos);

        // 为每个材质创建URP材质球并分配贴图
        foreach (var kvp in materialTextureMap)
        {
            string matName = kvp.Key;
            TextureInfo textureInfo = kvp.Value;

            string cleanMatName = matName.Replace(" (Instance)", "").Trim();
            string newMatPath = Path.Combine(materialPath, cleanMatName + ".mat");

            Material material = AssetDatabase.LoadAssetAtPath<Material>(newMatPath);

            // 查找URP/Lit shader
            Shader urpShader = Shader.Find("Universal Render Pipeline/Lit");
            if (urpShader == null)
            {
                Debug.LogWarning("URP/Lit shader not found! Trying alternative names...");
                urpShader = Shader.Find("Universal/Lit");
                if (urpShader == null)
                {
                    urpShader = Shader.Find("Lightweight Render Pipeline/Lit");
                }
            }

            if (urpShader == null)
            {
                Debug.LogError("Cannot find URP Lit shader! Using Standard shader instead.");
                urpShader = Shader.Find("Standard");
            }

            if (material == null)
            {
                // 创建新材质
                material = new Material(urpShader);
                material.name = cleanMatName;
                AssetDatabase.CreateAsset(material, newMatPath);
                Debug.Log($"Created URP material: {newMatPath}");
            }
            else
            {
                // 更新现有材质的shader
                material.shader = urpShader;
                Debug.Log($"Updated material shader: {newMatPath}");
            }

            // 分配贴图
            if (textureInfo != null)
            {
                // URP使用 _BaseMap 而不是 _MainTex
                material.SetTexture("_BaseMap", textureInfo.texture);
                material.SetColor("_BaseColor", Color.white);
                Debug.Log($"  Assigned texture: {textureInfo.name} ({textureInfo.size / 1024}KB) to {cleanMatName}");
            }
            else
            {
                Debug.LogWarning($"  No suitable texture found for {cleanMatName}");
            }

            EditorUtility.SetDirty(material);
        }

        AssetDatabase.SaveAssets();
        AssetDatabase.Refresh();

        Debug.Log($"\n=== FBX URP Material Setup Complete! ===");
        Debug.Log($"Created/Updated {materialNames.Count} materials in {materialPath}");
    }

    private static Dictionary<string, TextureInfo> CreateMaterialTextureMapping(
        List<string> materialNames,
        List<TextureInfo> textureInfos)
    {
        var mapping = new Dictionary<string, TextureInfo>();

        // 材质与贴图的智能匹配规则
        var materialKeywords = new Dictionary<string, string[]>
        {
            { "W_Cloth", new[] { "cloth", "fabric", "dress", "衣" } },
            { "W_Cloth_satin", new[] { "satin", "silk", "smooth", "丝" } },
            { "W_Cloth_tights", new[] { "tight", "leg", "pants", "裤" } },
            { "W_Hair00", new[] { "hair", "发" } },
            { "W_Hair02", new[] { "hair", "发" } },
            { "W_HairEyelash", new[] { "eyelash", "lash", "睫" } },
            { "W_Skin", new[] { "skin", "body", "face", "皮" } },
            { "eyeshadow1", new[] { "eye", "shadow", "眼" } }
        };

        // 为每个材质分配最大的未使用贴图（大贴图通常是主贴图）
        var usedTextures = new HashSet<TextureInfo>();

        foreach (var matName in materialNames)
        {
            string cleanMatName = matName.Replace(" (Instance)", "").Trim();

            // 尝试关键词匹配
            TextureInfo bestMatch = null;

            if (materialKeywords.ContainsKey(cleanMatName))
            {
                var keywords = materialKeywords[cleanMatName];
                bestMatch = textureInfos
                    .Where(t => !usedTextures.Contains(t))
                    .FirstOrDefault(t => keywords.Any(k =>
                        t.name.ToLower().Contains(k.ToLower())));
            }

            // 如果没有关键词匹配，使用最大的未使用贴图
            if (bestMatch == null)
            {
                bestMatch = textureInfos
                    .Where(t => !usedTextures.Contains(t))
                    .FirstOrDefault();
            }

            if (bestMatch != null)
            {
                mapping[matName] = bestMatch;
                usedTextures.Add(bestMatch);
            }
        }

        return mapping;
    }

    private class TextureInfo
    {
        public Texture2D texture;
        public string name;
        public string path;
        public long size;
    }
}

