                string textureName = Path.GetFileNameWithoutExtension(path).ToLower();
                textureDict[textureName] = texture;
            }
        }

        Debug.Log($"Found {textureDict.Count} textures in {texturePath}");

        // 获取所有子网格的渲染器
        Renderer[] renderers = fbx.GetComponentsInChildren<Renderer>(true);
        HashSet<string> materialNames = new HashSet<string>();

        foreach (var renderer in renderers)
        {
            foreach (var mat in renderer.sharedMaterials)
            {
                if (mat != null)
                {
                    materialNames.Add(mat.name);
                }
            }
        }

        Debug.Log($"Found {materialNames.Count} materials in FBX: {string.Join(", ", materialNames)}");

        // 为每个材质创建新的材质球并分配贴图
        foreach (string matName in materialNames)
        {
            string cleanMatName = matName.Replace(" (Instance)", "").Trim();
            string newMatPath = Path.Combine(materialPath, cleanMatName + ".mat");

            Material material = AssetDatabase.LoadAssetAtPath<Material>(newMatPath);
