                Debug.Log($"Created material: {newMatPath}");
            }

            // 尝试自动分配贴图
            // 查找包含材质名的贴图
            var matchingTextures = textureDict.Where(kvp =>
                kvp.Key.Contains(cleanMatName.ToLower()) ||
                cleanMatName.ToLower().Contains(kvp.Key)
            ).ToList();

            // 如果没有找到匹配的，尝试分配任意贴图（按字母顺序）
            if (matchingTextures.Count == 0)
            {
                matchingTextures = textureDict.Take(1).ToList();
            }

            if (matchingTextures.Count > 0)
            {
                var firstTexture = matchingTextures[0].Value;
                material.SetTexture("_MainTex", firstTexture);
                material.SetColor("_Color", Color.white);
                Debug.Log($"Assigned texture {matchingTextures[0].Key} to material {cleanMatName}");
            }

            EditorUtility.SetDirty(material);
        }

        AssetDatabase.SaveAssets();
        AssetDatabase.Refresh();

        // 现在应用材质到FBX
        foreach (string matName in materialNames)
        {
            string cleanMatName = matName.Replace(" (Instance)", "").Trim();
            string newMatPath = Path.Combine(materialPath, cleanMatName + ".mat");
            Material material = AssetDatabase.LoadAssetAtPath<Material>(newMatPath);

            if (material != null)
