        // 获取所有贴图文件
        string[] textureGuids = AssetDatabase.FindAssets("t:Texture2D", new[] { texturePath });
        List<TextureAnalysis> analyses = new List<TextureAnalysis>();

        Debug.Log("=== Analyzing Textures ===\n");

        foreach (string guid in textureGuids)
        {
            string path = AssetDatabase.GUIDToAssetPath(guid);
            Texture2D texture = AssetDatabase.LoadAssetAtPath<Texture2D>(path);

            if (texture != null)
            {
                // 确保贴图可读
                string textureName = Path.GetFileNameWithoutExtension(path);
                FileInfo fileInfo = new FileInfo(path);

                // 获取贴图导入设置
                TextureImporter importer = AssetImporter.GetAtPath(path) as TextureImporter;
                bool wasReadable = importer.isReadable;

                if (!wasReadable)
                {
                    importer.isReadable = true;
                    importer.SaveAndReimport();
                }

                // 分析贴图
                TextureAnalysis analysis = AnalyzeTexture(texture, textureName, fileInfo.Length);
                analyses.Add(analysis);

                // 恢复原始设置
                if (!wasReadable)
                {
                    importer.isReadable = false;
                    importer.SaveAndReimport();
                }

                Debug.Log($"{analysis.name} ({analysis.size / 1024}KB):\n" +
                         $"  Resolution: {texture.width}x{texture.height}\n" +
                         $"  Avg Color: R={analysis.avgColor.r:F2} G={analysis.avgColor.g:F2} B={analysis.avgColor.b:F2}\n" +
                         $"  Brightness: {analysis.brightness:F2}\n" +
                         $"  Estimated Type: {analysis.estimatedType}\n");
            }
        }

        Debug.Log("\n=== Texture Analysis Complete ===");
    }

    private static TextureAnalysis AnalyzeTexture(Texture2D texture, string name, long size)
    {
        var analysis = new TextureAnalysis
        {
            texture = texture,
            name = name,
            size = size
        };

        try
        {
            // 采样贴图颜色
            Color[] pixels = texture.GetPixels();

            if (pixels.Length > 0)
            {
                // 计算平均颜色
                float r = 0, g = 0, b = 0, a = 0;
                foreach (var pixel in pixels)
                {
                    r += pixel.r;
                    g += pixel.g;
                    b += pixel.b;
                    a += pixel.a;
                }

                int count = pixels.Length;
                analysis.avgColor = new Color(r / count, g / count, b / count, a / count);
                analysis.brightness = (analysis.avgColor.r + analysis.avgColor.g + analysis.avgColor.b) / 3f;

                // 估算贴图类型
                analysis.estimatedType = EstimateTextureType(analysis.avgColor, analysis.brightness, name);
            }
        }
        catch (System.Exception e)
        {
            Debug.LogWarning($"Could not read pixels from {name}: {e.Message}");
            analysis.estimatedType = "Unknown";
        }

        return analysis;
    }

    private static string EstimateTextureType(Color avgColor, float brightness, string name)
    {
        // 基于颜色和亮度推测贴图类型
        if (brightness < 0.15f)
            return "Hair/Dark (头发/暗色)";
        else if (brightness > 0.7f)
            return "Cloth/Light (服装/亮色)";
        else if (avgColor.r > 0.5f && avgColor.g < 0.4f)
            return "Skin/Warm (皮肤/暖色)";
        else if (avgColor.r < 0.3f && avgColor.g < 0.3f && avgColor.b < 0.3f)
            return "Hair/Eyelash (头发/睫毛)";
        else
            return "General (通用)";
    }

    private class TextureAnalysis
    {
        public Texture2D texture;
        public string name;
        public long size;
        public Color avgColor;
        public float brightness;
        public string estimatedType;
    }
}

