using System.Collections.Generic;
using ActionMachine.Runtime.Controller;
using NBC.ActionEditor;
using UnityEditor;
using UnityEngine;

namespace ActionMachine.Editor.GUIS
{
    public class ActionControllerWindow : EditorWindow
    {
        private const float WINDOW_WIDTH = 400f;
        private const float WINDOW_MIN_HEIGHT = 300f;
        private const float WINDOW_MAX_HEIGHT = 800f;

        private static ActionControllerWindow _instance;

        private SerializedObject _serializedController;
        private SerializedProperty _animatorProperty;
        private SerializedProperty _characterNodesProperty;

        public static ActionControllerWindow Instance
        {
            get
            {
                if (_instance == null)
                {
                    _instance = GetWindow<ActionControllerWindow>(true, "角色窗口", true);
                    _instance.minSize = new Vector2(WINDOW_WIDTH, WINDOW_MIN_HEIGHT);
                    _instance.maxSize = new Vector2(WINDOW_WIDTH, WINDOW_MAX_HEIGHT);
                }
                return _instance;
            }
        }

        public static void Show(Rect rect)
        {
            var window = Instance;
            Vector2 windowSize = new Vector2(WINDOW_WIDTH, Mathf.Max(rect.height, WINDOW_MIN_HEIGHT));
            Vector2 position = GUIUtility.GUIToScreenPoint(new Vector2(rect.x, rect.y));
            window.position = new Rect(position, windowSize);
            window.Show();
            window.Focus();
        }

        private void OnEnable()
        {
            _instance = this;
            InitializeSerializedProperties();
        }

        private void OnDestroy()
        {
            _serializedController?.Dispose();
            if (_instance == this)
            {
                _instance = null;
            }
        }

        private void InitializeSerializedProperties()
        {
            var controller = GetCurrentController();
            if (controller != null)
            {
                _serializedController = new SerializedObject(controller);
                _animatorProperty = _serializedController.FindProperty("animator");
                _characterNodesProperty = _serializedController.FindProperty("characterNodes");
            }
        }

        private ActionCharacterController GetCurrentController()
        {
            return AssetPlayer.Inst.SelectSceneGameObject as ActionCharacterController;
        }

        private void OnGUI()
        {
            var controller = GetCurrentController();
            if (controller == null)
            {
                EditorGUILayout.HelpBox("请选择一个 ActionController 对象", MessageType.Info);
                return;
            }

            // 如果序列化对象未初始化或目标已改变，重新初始化
            if (_serializedController == null || _serializedController.targetObject != controller)
            {
                InitializeSerializedProperties();
            }

            if (_serializedController == null) return;

            _serializedController.Update();

            DrawHeader();
            DrawAnimatorField();
            DrawCharacterNodes();
            DrawActionButtons(controller);

            _serializedController.ApplyModifiedProperties();
        }

        private void DrawHeader()
        {
            GUILayout.BeginVertical("box");
            GUI.color = new Color(0, 0, 0, 0.3f);
            GUILayout.BeginHorizontal(Styles.HeaderBoxStyle);
            GUI.color = Color.white;
            GUILayout.Label($"<size=22><b>角色窗口</b></size>", ActionControllerWindowStyles.RichTextLabel);
            GUILayout.EndHorizontal();
            GUILayout.Space(2);
            GUILayout.EndVertical();
        }

        private void DrawAnimatorField()
        {
            GUILayout.Label("动画控制器:", EditorStyles.boldLabel);
            EditorGUILayout.PropertyField(_animatorProperty, new GUIContent("Animator"), true);
            GUILayout.Space(10);
        }

        private void DrawCharacterNodes()
        {
            GUILayout.Label("角色节点:", EditorStyles.boldLabel);

            if (_characterNodesProperty == null)
            {
                EditorGUILayout.HelpBox("characterNodes 属性未找到", MessageType.Warning);
                return;
            }

            GUILayout.BeginVertical("box");

            // 显示角色节点列表
            for (int i = 0; i < _characterNodesProperty.arraySize; i++)
            {
                DrawCharacterNodeElement(i);
            }

            DrawAddButton();

            GUILayout.EndVertical();
        }

        private void DrawCharacterNodeElement(int index)
        {
            var element = _characterNodesProperty.GetArrayElementAtIndex(index);

            GUILayout.BeginHorizontal();

            // 绘制类型枚举
            var typeProperty = element.FindPropertyRelative("type");
            if (typeProperty != null)
            {
                EditorGUILayout.PropertyField(typeProperty, GUIContent.none, GUILayout.Width(120));
            }

            // 绘制 Transform 引用
            var bindTransformProperty = element.FindPropertyRelative("bindTransform");
            if (bindTransformProperty != null)
            {
                EditorGUILayout.PropertyField(bindTransformProperty, GUIContent.none);
            }

            // 删除按钮
            GUI.color = Color.red;
            if (GUILayout.Button("X", GUILayout.Width(25)))
            {
                _characterNodesProperty.DeleteArrayElementAtIndex(index);
            }
            GUI.color = Color.white;

            GUILayout.EndHorizontal();
        }

        private void DrawAddButton()
        {
            GUILayout.Space(5);
            GUI.color = Color.green;
            if (GUILayout.Button("添加节点", GUILayout.Height(25)))
            {
                _characterNodesProperty.arraySize++;
            }
            GUI.color = Color.white;
        }

        private void DrawActionButtons(ActionCharacterController characterController)
        {
            GUILayout.Space(10);
            GUILayout.BeginHorizontal();

            GUI.color = new Color(1f, 0.7f, 0.2f);
            if (GUILayout.Button("T Pose", GUILayout.Height(30), GUILayout.Width(120)))
            {
                SetTPose(characterController);
            }
            GUI.color = Color.white;

            GUILayout.FlexibleSpace();

            GUI.color = Color.cyan;
            if (GUILayout.Button("保存为 Prefab", GUILayout.Height(30), GUILayout.Width(120)))
            {
                SaveAsPrefab(characterController);
            }
            GUI.color = Color.white;

            GUILayout.EndHorizontal();
        }

        private void SetTPose(ActionCharacterController characterController)
        {
            if (characterController == null)
            {
                EditorUtility.DisplayDialog("错误", "ActionController 为空", "确定");
                return;
            }

            if (characterController.Animator == null)
            {
                EditorUtility.DisplayDialog("错误", "未找到 AnimancerComponent 组件", "确定");
                return;
            }
            Animator animator = characterController.Animator.Animator;
            if (animator == null)
            {
                EditorUtility.DisplayDialog("错误", "未找到 Animator 组件", "确定");
                return;
            }

            if (animator.avatar == null || !animator.avatar.isHuman)
            {
                EditorUtility.DisplayDialog("错误", "该模型不是 Humanoid 类型，无法使用 T Pose", "确定");
                return;
            }

            Undo.RegisterFullObjectHierarchyUndo(characterController.gameObject, "Set T Pose");

            HumanPoseHandler humanPoseHandler = new HumanPoseHandler(animator.avatar, animator.transform);
            HumanPose humanPose = new HumanPose();

            humanPoseHandler.GetHumanPose(ref humanPose);

            for (int i = 0; i < humanPose.muscles.Length; i++)
            {
                humanPose.muscles[i] = 0f;
            }

            humanPoseHandler.SetHumanPose(ref humanPose);

            EditorUtility.SetDirty(characterController.gameObject);

            Debug.Log($"已将 {characterController.gameObject.name} 设置为 T Pose");
        }

        private void SaveAsPrefab(ActionCharacterController characterController)
        {
            if (characterController == null)
            {
                EditorUtility.DisplayDialog("错误", "ActionController 为空，无法保存", "确定");
                return;
            }

            // 获取当前对象的 GameObject
            GameObject gameObject = characterController.gameObject;

            // 打开保存文件面板
            string path = EditorUtility.SaveFilePanelInProject(
                "保存 Prefab",
                $"{gameObject.name}_Prefab",
                "prefab",
                "请选择保存 Prefab 的位置");

            if (string.IsNullOrEmpty(path))
                return;

            try
            {
                // 检查是否已经是 Prefab 实例
                bool wasPrefabInstance = PrefabUtility.IsPartOfPrefabInstance(gameObject);

                // 保存为 Prefab
                Object prefab;
                if (wasPrefabInstance)
                {
                    // 如果是 Prefab 实例，连接到现有 Prefab
                    prefab = PrefabUtility.SaveAsPrefabAssetAndConnect(
                        gameObject, 
                        path, 
                        InteractionMode.UserAction);
                }
                else
                {
                    // 如果是场景对象，创建新的 Prefab
                    prefab = PrefabUtility.SaveAsPrefabAsset(gameObject, path);
                }

                if (prefab != null)
                {
                    EditorUtility.DisplayDialog("成功", $"Prefab 已保存到: {path}", "确定");
                    Debug.Log($"Prefab 保存成功: {path}");

                    // 选中新创建的 Prefab
                    Selection.activeObject = prefab;
                }
                else
                {
                    EditorUtility.DisplayDialog("错误", "Prefab 保存失败", "确定");
                }
            }
            catch (System.Exception e)
            {
                EditorUtility.DisplayDialog("错误", $"保存 Prefab 时发生错误: {e.Message}", "确定");
                Debug.LogError($"保存 Prefab 失败: {e}");
            }
        }
    }

    // 辅助样式类
    public static class ActionControllerWindowStyles
    {
        public static GUIStyle HeaderBoxStyle => new GUIStyle(GUI.skin.box)
        {
            padding = new RectOffset(10, 10, 5, 5)
        };

        public static GUIStyle RichTextLabel => new GUIStyle(EditorStyles.label)
        {
            richText = true
        };
    }
}

