using System;
using System.Reflection;
using UnityEditor;
using UnityEngine;
using NBC.ActionEditor;

namespace ActionMachine.Editor
{
    /// <summary>
    /// 编辑器窗口管理器 - 一键打开双窗口布局（停靠模式）
    /// </summary>
    public static class AutoOpenWindows
    {
        /// <summary>
        /// 打开双窗口布局（ActionEditor + InspectorPreview），并停靠在一起
        /// </summary>
        [MenuItem("NBC/Action Editor/Open Dual Windows", false, 1)]
        public static void OpenDualWindows()
        {
            // 打开主时间轴编辑器
            ActionEditorWindow.OpenDirectorWindow();

            // 打开检查器窗口
            InspectorPreviewWindow.OpenWindow();

            // 延迟执行停靠布局，等待窗口完全创建
            EditorApplication.delayCall += DockWindowsTogether;
        }

        /// <summary>
        /// 将两个窗口停靠在一起
        /// 使用Unity内部的Docking API实现窗口合并
        /// </summary>
        private static void DockWindowsTogether()
        {
            try
            {
                // 获取两个窗口实例
                var actionWindow = ActionEditorWindow.Instance;
                var inspectorWindow = EditorWindow.GetWindow<InspectorPreviewWindow>("预览检查器", false);

                if (actionWindow == null || inspectorWindow == null)
                {
                    Debug.LogWarning("无法获取窗口实例，停靠失败");
                    return;
                }

                // 使用反射调用Unity内部停靠方法
                DockWindow(inspectorWindow, actionWindow, DockPosition.Right);

                Debug.Log("窗口停靠成功：InspectorPreview 已停靠到 ActionEditor 右侧");
            }
            catch (Exception ex)
            {
                Debug.LogError($"窗口停靠失败: {ex.Message}\n{ex.StackTrace}");
            }
        }

        /// <summary>
        /// 停靠位置枚举
        /// </summary>
        private enum DockPosition
        {
            Left,
            Right,
            Top,
            Bottom
        }

        /// <summary>
        /// 将窗口停靠到目标窗口的指定位置
        /// 使用反射访问Unity内部API
        /// </summary>
        /// <param name="windowToDock">要停靠的窗口</param>
        /// <param name="targetWindow">目标窗口</param>
        /// <param name="position">停靠位置</param>
        private static void DockWindow(EditorWindow windowToDock, EditorWindow targetWindow, DockPosition position)
        {
            // 获取UnityEditor.DockArea类型
            var dockAreaType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.DockArea");
            if (dockAreaType == null)
            {
                Debug.LogError("无法找到 UnityEditor.DockArea 类型");
                return;
            }

            // 获取目标窗口的父容器（DockArea）
            var parentField = typeof(EditorWindow).GetField("m_Parent", BindingFlags.Instance | BindingFlags.NonPublic);
            if (parentField == null)
            {
                Debug.LogError("无法获取 EditorWindow.m_Parent 字段");
                return;
            }

            var targetDockArea = parentField.GetValue(targetWindow);
            if (targetDockArea == null)
            {
                Debug.LogError("目标窗口没有父容器");
                return;
            }

            // 获取要停靠窗口的父容器
            var windowToDockArea = parentField.GetValue(windowToDock);
            if (windowToDockArea == null)
            {
                Debug.LogError("要停靠的窗口没有父容器");
                return;
            }

            // 使用AddTab方法将窗口添加到目标DockArea
            var addTabMethod = dockAreaType.GetMethod("AddTab",
                BindingFlags.Instance | BindingFlags.Public | BindingFlags.NonPublic,
                null,
                new Type[] { typeof(EditorWindow), typeof(bool) },
                null);

            if (addTabMethod != null)
            {
                // 直接添加为标签页（最简单的方式）
                addTabMethod.Invoke(targetDockArea, new object[] { windowToDock, true });
            }
            else
            {
                // 备用方案：尝试使用SplitView进行分割停靠
                TryDockWithSplitView(windowToDock, targetWindow, targetDockArea, position);
            }
        }

        /// <summary>
        /// 备用停靠方案：使用SplitView分割窗口
        /// </summary>
        private static void TryDockWithSplitView(EditorWindow windowToDock, EditorWindow targetWindow,
            object targetDockArea, DockPosition position)
        {
            try
            {
                var splitViewType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.SplitView");
                var dockAreaType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.DockArea");

                if (splitViewType == null || dockAreaType == null)
                {
                    Debug.LogError("无法找到 SplitView 或 DockArea 类型");
                    return;
                }

                // 获取父容器的parent（通常是SplitView）
                var parentProperty = dockAreaType.GetProperty("parent", BindingFlags.Instance | BindingFlags.Public);
                if (parentProperty != null)
                {
                    var splitView = parentProperty.GetValue(targetDockArea, null);

                    // 这里可以进一步实现SplitView的分割逻辑
                    // 由于Unity内部API复杂，简化为直接添加标签页
                    Debug.LogWarning("SplitView 停靠方法需要更复杂的实现，改用标签页模式");
                }
            }
            catch (Exception ex)
            {
                Debug.LogError($"SplitView 停靠失败: {ex.Message}");
            }
        }
    }
}

