using System;
using System.Reflection;
using UnityEditor;
using UnityEngine;
using NBC.ActionEditor;

namespace ActionMachine.Editor
{
    /// <summary>
    /// 编辑器窗口管理器 - 一键打开双窗口布局（停靠模式）
    /// </summary>
    public static class AutoOpenWindows
    {
        /// <summary>
        /// 打开双窗口布局（ActionEditor + InspectorPreview），并停靠在一起
        /// </summary>
        [MenuItem("NBC/Action Editor/Open Dual Windows", false, 1)]
        public static void OpenDualWindows()
        {
            // 打开主时间轴编辑器
            ActionEditorWindow.OpenDirectorWindow();

            // 打开检查器窗口
            InspectorPreviewWindow.OpenWindow();

            // 延迟执行停靠布局，等待窗口完全创建
            EditorApplication.delayCall += DockWindowsTogether;
        }

        /// <summary>
        /// 将两个窗口停靠在一起
        /// 使用Unity公开API EditorWindow实现停靠
        /// </summary>
        private static void DockWindowsTogether()
        {
            try
            {
                // 获取两个窗口实例
                var actionWindow = ActionEditorWindow.Instance;
                if (actionWindow == null)
                {
                    Debug.LogWarning("ActionEditor窗口未打开");
                    return;
                }

                // 使用GetWindow并指定dock参数
                // utility=false表示正常窗口，focus=false表示不聚焦
                var inspectorWindow = EditorWindow.GetWindow<InspectorPreviewWindow>(
                    utility: false,
                    title: "预览检查器",
                    focus: false);

                if (inspectorWindow == null)
                {
                    Debug.LogWarning("无法创建Inspector窗口");
                    return;
                }

                // 延迟执行停靠，确保窗口已完全创建
                EditorApplication.delayCall += () =>
                {
                    try
                    {
                        // 使用反射调用DockArea的停靠方法
                        DockWindowNext(inspectorWindow, actionWindow);
                    }
                    catch (Exception ex)
                    {
                        Debug.LogWarning($"停靠失败，使用备用方案: {ex.Message}");
                        // 备用方案：设置窗口位置
                        SetWindowPositions(actionWindow, inspectorWindow);
                    }
                };

                Debug.Log("窗口停靠命令已发送");
            }
            catch (Exception ex)
            {
                Debug.LogError($"窗口停靠失败: {ex.Message}\n{ex.StackTrace}");
            }
        }

        /// <summary>
        /// 停靠位置枚举
        /// </summary>
        private enum DockPosition
        {
            Left,
            Right,
            Top,
            Bottom
        }

        /// <summary>
        /// 使用SplitView将窗口停靠到目标窗口的指定位置
        /// 实现真正的左右分割显示
        /// </summary>
        /// <param name="windowToDock">要停靠的窗口</param>
        /// <param name="targetWindow">目标窗口</param>
        /// <param name="position">停靠位置</param>
        private static void DockWindowWithSplit(EditorWindow windowToDock, EditorWindow targetWindow, DockPosition position)
        {
            // 获取必要的类型
            var containerWindowType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.ContainerWindow");
            var splitViewType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.SplitView");
            var dockAreaType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.DockArea");
            var viewType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.View");

            if (containerWindowType == null || splitViewType == null || dockAreaType == null || viewType == null)
            {
                Debug.LogError("无法找到必要的Unity内部类型");
                return;
            }

            // 获取窗口的父容器
            var parentField = typeof(EditorWindow).GetField("m_Parent", BindingFlags.Instance | BindingFlags.NonPublic);
            if (parentField == null)
            {
                Debug.LogError("无法获取 m_Parent 字段");
                return;
            }

            var targetDockArea = parentField.GetValue(targetWindow);
            var windowToDockArea = parentField.GetValue(windowToDock);

            if (targetDockArea == null || windowToDockArea == null)
            {
                Debug.LogError("无法获取窗口的 DockArea");
                return;
            }

            // 获取父视图的parent属性
            var parentProperty = viewType.GetProperty("parent", BindingFlags.Instance | BindingFlags.Public);
            if (parentProperty == null)
            {
                Debug.LogError("无法获取 parent 属性");
                return;
            }

            var parentView = parentProperty.GetValue(targetDockArea, null);

            // 尝试分割当前视图
            try
            {
                // 获取 SplitView.SplitMode 枚举
                var splitModeType = splitViewType.GetNestedType("SplitMode", BindingFlags.Public);
                if (splitModeType == null)
                {
                    Debug.LogWarning("无法获取 SplitMode 枚举类型");
                    return;
                }

                // 根据位置确定分割模式 (0=Vertical, 1=Horizontal)
                int splitMode = (position == DockPosition.Left || position == DockPosition.Right) ? 0 : 1;

                // 如果父视图不是SplitView，尝试创建
                if (parentView == null || parentView.GetType() != splitViewType)
                {
                    Debug.LogWarning("父视图不是 SplitView，尝试简化方案");

                    // 简化方案：直接设置窗口位置
                    SetWindowPositions(actionWindow: targetWindow, inspectorWindow: windowToDock);
                    return;
                }

                // 调用 SplitView 的添加方法
                var addChildMethod = splitViewType.GetMethod("AddChild",
                    BindingFlags.Instance | BindingFlags.Public | BindingFlags.NonPublic);

                if (addChildMethod != null)
                {
                    addChildMethod.Invoke(parentView, new object[] { windowToDockArea });
                    Debug.Log("成功添加到 SplitView");
                }
            }
            catch (Exception ex)
            {
                Debug.LogWarning($"SplitView 分割失败，使用备用方案: {ex.Message}");

                // 备用方案：设置窗口位置模拟分割效果
                SetWindowPositions(actionWindow: targetWindow, inspectorWindow: windowToDock);
            }
        }

        /// <summary>
        /// 备用方案：通过设置窗口位置模拟分割效果
        /// 使两个窗口紧密贴合，视觉上接近合并效果
        /// </summary>
        private static void SetWindowPositions(EditorWindow actionWindow, EditorWindow inspectorWindow)
        {
            // 获取主显示器尺寸
            float screenWidth = Screen.currentResolution.width;
            float screenHeight = Screen.currentResolution.height;

            // 计算窗口尺寸 - 左侧65%，右侧33%
            float totalWidth = screenWidth * 0.98f;  // 使用98%屏幕宽度
            float leftWidth = totalWidth * 0.66f;     // 左侧占66%
            float rightWidth = totalWidth * 0.34f;    // 右侧占34%
            float windowHeight = screenHeight * 0.75f;
            float topMargin = 60f;
            float leftMargin = 10f;

            // 设置ActionEditor窗口位置（左侧）
            actionWindow.position = new Rect(leftMargin, topMargin, leftWidth, windowHeight);

            // 设置InspectorPreview窗口位置（右侧紧贴，无间隙）
            // 使用leftMargin + leftWidth，完全贴合
            inspectorWindow.position = new Rect(leftMargin + leftWidth, topMargin, rightWidth, windowHeight);

            // 聚焦到ActionEditor
            actionWindow.Focus();

            Debug.Log($"窗口布局完成 - ActionEditor: {leftWidth}px, InspectorPreview: {rightWidth}px");
        }

    }
}

