private static void DockWindowNext(EditorWindow windowToDock, EditorWindow targetWindow)
{
    // 获取必要的类型
    var dockAreaType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.DockArea");
    var splitViewType = typeof(UnityEditor.Editor).Assembly.GetType("UnityEditor.SplitView");

    if (dockAreaType == null || splitViewType == null)
    {
        throw new Exception("无法找到 DockArea 或 SplitView 类型");
    }

    // 获取窗口的父DockArea
    var parentField = typeof(EditorWindow).GetField("m_Parent", BindingFlags.Instance | BindingFlags.NonPublic);
    if (parentField == null)
    {
        throw new Exception("无法获取 m_Parent 字段");
    }

    var targetDockArea = parentField.GetValue(targetWindow);
    var windowDockArea = parentField.GetValue(windowToDock);

    if (targetDockArea == null || windowDockArea == null)
    {
        throw new Exception("无法获取窗口的 DockArea");
    }

    // 方法1: 尝试使用DockArea.s_OriginalDragSource来触发停靠
    // 这是Unity用来处理窗口拖拽停靠的机制
    var originalDragSourceField = dockAreaType.GetField("s_OriginalDragSource",
        BindingFlags.Static | BindingFlags.NonPublic);

    if (originalDragSourceField != null)
    {
        // 设置拖拽源为要停靠的窗口
        originalDragSourceField.SetValue(null, windowDockArea);
    }

    // 方法2: 直接调用DockArea.AddTab将窗口添加到目标DockArea
    // 但设置sendPaneEvent=false避免触发不必要的事件
    var addTabMethod = dockAreaType.GetMethod("AddTab",
        BindingFlags.Instance | BindingFlags.Public | BindingFlags.NonPublic,
        null,
        new Type[] { typeof(EditorWindow), typeof(bool) },
        null);

    if (addTabMethod == null)
    {
        // 尝试只有一个参数的重载
        addTabMethod = dockAreaType.GetMethod("AddTab",
            BindingFlags.Instance | BindingFlags.Public | BindingFlags.NonPublic,
            null,
            new Type[] { typeof(EditorWindow) },
            null);
    }

    if (addTabMethod != null)
    {
        // 添加到同一个DockArea（会创建标签页）
        if (addTabMethod.GetParameters().Length == 2)
        {
            addTabMethod.Invoke(targetDockArea, new object[] { windowToDock, false });
        }
        else
        {
            addTabMethod.Invoke(targetDockArea, new object[] { windowToDock });
        }

        Debug.Log("窗口已停靠到目标窗口的DockArea");
    }
    else
    {
        throw new Exception("无法找到 AddTab 方法");
    }
}

