using System;
using System.Reflection;
using UnityEditor;
using UnityEngine;
using NBC.ActionEditor;

namespace ActionMachine.Editor
{
    /// <summary>
    /// 双面板编辑器窗口 - 左侧时间轴编辑器，右侧属性检查器
    /// 模拟图片中的一个窗口内左右分割的效果
    /// </summary>
    public class DualEditorWindow : EditorWindow
    {
        private float _splitRatio = 0.65f; // 左右分割比例
        private bool _isResizing = false;
        private Rect _splitterRect;
        private const float SPLITTER_WIDTH = 5f;

        private ActionEditorWindow _actionEditorWindow;
        private InspectorPreviewWindow _inspectorWindow;

        private MethodInfo _actionEditorOnGUI;
        private MethodInfo _inspectorOnGUI;

        [MenuItem("NBC/Action Editor/Open Dual Editor Window", false, 0)]
        public static void OpenDualEditorWindow()
        {
            var window = GetWindow<DualEditorWindow>("预览检查器");
            window.minSize = new Vector2(800, 400);
            window.Show();
        }

        private void OnEnable()
        {
            // 确保ActionEditor已打开
            ActionEditorWindow.OpenDirectorWindow();

            // 获取或创建窗口实例（但不显示）
            _actionEditorWindow = ActionEditorWindow.Instance;
            _inspectorWindow = CreateInstance<InspectorPreviewWindow>();

            // 通过反射获取OnGUI方法
            _actionEditorOnGUI = typeof(ActionEditorWindow).GetMethod("OnGUI",
                BindingFlags.Instance | BindingFlags.NonPublic | BindingFlags.Public);

            _inspectorOnGUI = typeof(InspectorPreviewWindow).GetMethod("OnGUI",
                BindingFlags.Instance | BindingFlags.NonPublic | BindingFlags.Public);
        }

        private void OnDisable()
        {
            // 清理
            if (_inspectorWindow != null)
            {
                DestroyImmediate(_inspectorWindow);
            }
        }

        private void OnGUI()
        {
            // 绘制工具栏
            DrawToolbar();

            // 创建水平布局
            EditorGUILayout.BeginHorizontal(GUILayout.ExpandWidth(true), GUILayout.ExpandHeight(true));

            // 左侧面板 - ActionEditor时间轴
            DrawLeftPanel();

            // 绘制可拖动的分割线
            DrawSplitter();

            // 右侧面板 - Inspector属性检查器
            DrawRightPanel();

            EditorGUILayout.EndHorizontal();

            // 处理分割线拖动
            HandleSplitterEvents();
        }

        /// <summary>
        /// 绘制顶部工具栏
        /// </summary>
        private void DrawToolbar()
        {
            EditorGUILayout.BeginHorizontal(EditorStyles.toolbar);

            if (GUILayout.Button("刷新", EditorStyles.toolbarButton, GUILayout.Width(50)))
            {
                // 刷新窗口
                Repaint();
            }

            GUILayout.FlexibleSpace();

            GUILayout.Label($"分割比例: {(_splitRatio * 100):F0}%", EditorStyles.miniLabel);

            EditorGUILayout.EndHorizontal();
        }

        /// <summary>
        /// 绘制左侧面板（ActionEditor内容）
        /// </summary>
        private void DrawLeftPanel()
        {
            float leftWidth = position.width * _splitRatio - SPLITTER_WIDTH / 2;

            EditorGUILayout.BeginVertical(GUILayout.Width(leftWidth), GUILayout.ExpandHeight(true));

            // 绘制左侧标题
            GUILayout.Label("动作编辑器", EditorStyles.boldLabel);
            EditorGUILayout.Space(2);

            // 尝试绘制ActionEditor的内容
            if (_actionEditorWindow != null && _actionEditorOnGUI != null)
            {
                try
                {
                    // 创建一个临时rect区域
                    Rect leftRect = GUILayoutUtility.GetRect(leftWidth, position.height - 40);

                    // 保存当前GUI状态
                    var savedClip = GUI.enabled;

                    // 使用反射调用ActionEditor的OnGUI
                    // 注意：这可能不会完美工作，因为OnGUI依赖窗口上下文
                    GUILayout.BeginArea(leftRect);

                    // 由于直接调用OnGUI可能有问题，我们显示提示信息
                    EditorGUILayout.HelpBox(
                        "ActionEditor时间轴编辑器\n\n" +
                        "请使用菜单 'NBC > Action Editor > Open Action Editor' 打开独立的时间轴窗口。\n\n" +
                        "由于Unity窗口系统的限制，暂时无法在此窗口中直接嵌入完整的时间轴编辑器。",
                        MessageType.Info);

                    GUILayout.EndArea();

                    GUI.enabled = savedClip;
                }
                catch (Exception ex)
                {
                    EditorGUILayout.HelpBox($"绘制ActionEditor失败: {ex.Message}", MessageType.Error);
                }
            }
            else
            {
                EditorGUILayout.HelpBox("ActionEditor未初始化", MessageType.Warning);
            }

            EditorGUILayout.EndVertical();
        }

        /// <summary>
        /// 绘制分割线
        /// </summary>
        private void DrawSplitter()
        {
            _splitterRect = new Rect(
                position.width * _splitRatio - SPLITTER_WIDTH / 2,
                0,
                SPLITTER_WIDTH,
                position.height);

            // 绘制分割线
            EditorGUI.DrawRect(_splitterRect, new Color(0.1f, 0.1f, 0.1f));

            // 设置鼠标光标
            EditorGUIUtility.AddCursorRect(_splitterRect, MouseCursor.ResizeHorizontal);
        }

        /// <summary>
        /// 绘制右侧面板（Inspector内容）
        /// </summary>
        private void DrawRightPanel()
        {
            float rightWidth = position.width * (1 - _splitRatio) - SPLITTER_WIDTH / 2;

            EditorGUILayout.BeginVertical(GUILayout.Width(rightWidth), GUILayout.ExpandHeight(true));

            // 绘制右侧标题
            GUILayout.Label("技能资源 基础信息", EditorStyles.boldLabel);
            EditorGUILayout.Space(2);

            // 尝试绘制Inspector的内容
            if (_inspectorWindow != null && _inspectorOnGUI != null)
            {
                try
                {
                    Rect rightRect = GUILayoutUtility.GetRect(rightWidth, position.height - 40);

                    GUILayout.BeginArea(rightRect);

                    // 由于技术限制，显示提示信息
                    EditorGUILayout.HelpBox(
                        "属性检查器\n\n" +
                        "请使用菜单 'NBC > Action Editor > 预览检查器' 打开独立的检查器窗口。\n\n" +
                        "由于Unity窗口系统的限制，暂时无法在此窗口中直接嵌入完整的检查器。",
                        MessageType.Info);

                    GUILayout.EndArea();
                }
                catch (Exception ex)
                {
                    EditorGUILayout.HelpBox($"绘制Inspector失败: {ex.Message}", MessageType.Error);
                }
            }
            else
            {
                EditorGUILayout.HelpBox("Inspector未初始化", MessageType.Warning);
            }

            EditorGUILayout.EndVertical();
        }

        /// <summary>
        /// 处理分割线拖动事件
        /// </summary>
        private void HandleSplitterEvents()
        {
            Event e = Event.current;

            switch (e.type)
            {
                case EventType.MouseDown:
                    if (_splitterRect.Contains(e.mousePosition))
                    {
                        _isResizing = true;
                        e.Use();
                    }
                    break;

                case EventType.MouseUp:
                    _isResizing = false;
                    break;

                case EventType.MouseDrag:
                    if (_isResizing)
                    {
                        // 更新分割比例
                        _splitRatio = Mathf.Clamp(e.mousePosition.x / position.width, 0.2f, 0.8f);
                        Repaint();
                        e.Use();
                    }
                    break;
            }
        }

        private void Update()
        {
            // 定期重绘
            if (EditorApplication.timeSinceStartup % 0.1f < 0.05f)
            {
                Repaint();
            }
        }
    }
}

