﻿using ActionMachine.Runtime.Controller;
using ActionMachine.Runtime.Directables;
using Animancer;
using NBC.ActionEditor;
using UnityEngine;

namespace ActionMachine.Editor.Preview
{
    [CustomPreview(typeof(AnimancerClip))]
    public class AnimancerClipPreview : PreviewBase<AnimancerClip>
    {
        private AnimancerComponent _animator;
        private AnimancerState _currentRunState;
        private StringReference _clipStringReference;
        private int _layerIndex = -1; // 当前使用的 Layer 索引

        protected override void OnSetSelectTarget(INBCActionController target)
        {
            _animator = ((ActionCharacterController)target).Animator;
            if (_animator == null)
            {
                Debug.LogError($"The {nameof(target)} is not Animator.");
            }
        }

        protected override void OnUpdate(float time, float previousTime)
        {
            if (_animator == null)
            {
                return;
            }

            if (_currentRunState == null)
            {
                return;
            }

            Preview(time);
        }

        public override void Enter(bool isReverse)
        {
            base.Enter(isReverse);
            if (_animator == null)
            {
                return;
            }

            if (_animator.Transitions != clip.EditorAssets)
            {
                Debug.LogError("The Transitions is not match.");
                return;
            }

            _clipStringReference = StringReference.Get(clip.ClipName);

            // 为每个 clip 分配独立的 Layer
            _layerIndex = AllocateLayer();
            var layer = _animator.Layers[_layerIndex];

            // 在指定 Layer 上播放动画
            _currentRunState = layer.TryPlay(_clipStringReference);
            if (_currentRunState == null)
            {
                Debug.LogError($"The clip is not found. name '{clip.ClipName}'");
                return;
            }

            _currentRunState.Speed = 0;

            // 计算初始权重
            UpdateLayerWeight(clip.StartTime);

#if UNITY_EDITOR
            Debug.Log($"[AnimancerLayer] Clip '{clip.ClipName}' on Layer {_layerIndex}, Weight: {layer.Weight:F2}");
#endif
        }

        public override void Exit(bool isReverse)
        {
            base.Exit(isReverse);

            if (_animator == null)
            {
                return;
            }

            // 停止播放并释放 Layer
            if (_layerIndex >= 0 && _layerIndex < _animator.Layers.Count)
            {
                var layer = _animator.Layers[_layerIndex];
                layer.Stop();
                layer.Weight = 0; // 重置权重

#if UNITY_EDITOR
                Debug.Log($"[AnimancerLayer] Released Layer {_layerIndex} for clip '{clip.ClipName}'");
#endif
            }

            _currentRunState = null;
            _layerIndex = -1;
        }

        private void Preview(float currentTime)
        {
            var percentage = clip.ToPercentage(currentTime);
            _currentRunState.MoveTime(percentage, true);

            // 动态更新 Layer 权重以实现淡入淡出
            UpdateLayerWeight(currentTime);
        }

        /// <summary>
        /// 分配一个可用的 Layer（查找权重为0的 Layer）
        /// </summary>
        private int AllocateLayer()
        {
            // 确保有足够的 Layers
            while (_animator.Layers.Count < 4) // 最多支持 4 个同时混合
            {
                _animator.Layers.Add();
            }

            // 查找未使用的 Layer（权重为 0 或接近 0）
            for (int i = 1; i < _animator.Layers.Count; i++) // 跳过 Layer 0（基础层）
            {
                if (_animator.Layers[i].Weight < 0.01f)
                {
                    return i;
                }
            }

            // 如果都在使用，创建新 Layer
            _animator.Layers.Add();
            return _animator.Layers.Count - 1;
        }

        /// <summary>
        /// 根据时间计算并更新 Layer 权重（淡入淡出）
        /// </summary>
        private void UpdateLayerWeight(float currentTime)
        {
            if (_layerIndex < 0 || _layerIndex >= _animator.Layers.Count)
                return;

            float normalizedTime = (currentTime - clip.StartTime) / clip.Length;
            float weight = CalculateBlendWeight(normalizedTime);

            var layer = _animator.Layers[_layerIndex];
            layer.Weight = weight;
        }

        /// <summary>
        /// 计算混合权重（带淡入淡出）
        /// </summary>
        private float CalculateBlendWeight(float normalizedTime)
        {
            const float fadeInDuration = 0.15f;  // 15% 淡入
            const float fadeOutDuration = 0.15f; // 15% 淡出

            float weight = 1f;

            // 淡入
            if (normalizedTime < fadeInDuration)
            {
                weight = Mathf.SmoothStep(0f, 1f, normalizedTime / fadeInDuration);
            }
            // 淡出
            else if (normalizedTime > 1f - fadeOutDuration)
            {
                float fadeOutProgress = (1f - normalizedTime) / fadeOutDuration;
                weight = Mathf.SmoothStep(0f, 1f, fadeOutProgress);
            }

            return Mathf.Clamp01(weight);
        }

        public override void OnDestroy()
        {
            base.OnDestroy();
            if (_currentRunState != null && _animator != null)
            {
                if (_layerIndex >= 0 && _layerIndex < _animator.Layers.Count)
                {
                    _animator.Layers[_layerIndex].Stop();
                    _animator.Layers[_layerIndex].Weight = 0;
                }
            }
        }
    }
}

