 using System;
 using Facebook.Unity;
 using GameFramework;
using LFramework.Runtime;
using ThinkingAnalytics;
using ThinkingData.Analytics;
using UnityEngine;
using UnityEngine.U2D;
using UnityGameFramework.Runtime;
using Zenject;
using Object = UnityEngine.Object;

namespace MagicWarrior.Runtime
{
    public class MagicWarriorApplication : LSystemApplicationBehaviour
    {
        [Inject] private ResourceComponent ResourceComponent { get; }
        [Inject] private EventComponent EventComponent { get; }

        private DateTime _enterGameDateTime;

        protected override void Awake()
        {
            LaunchAnalyticsHelper.Track(TrackProgressEnum.FirstAwake, "First Awake Enter.");
            _enterGameDateTime = DateTime.Now;
            InitializeAnalyticsWhenFirstAwake();
            InitializeAppsFlyer();
            InitializeAdjust();
            LaunchAnalyticsHelper.FirstOpen();
            RuntimeFaceBookHelper.InitializeFaceBook();
            FirebaseHelper.InitSdk();
            DevicePerformanceHelper.InitDevicePerformanceLevel(gameSetting);
            base.Awake();
        }
        

        protected override void RegisterSetting()
        {
            base.RegisterSetting();
        }

        protected override void RegisterComponents()
        {
            base.RegisterComponents();
            LaunchAnalyticsHelper.Track(TrackProgressEnum.RegisterComponent, "Register Components.");
        }

        protected override void BindComponents()
        {
            base.BindComponents();
            LaunchAnalyticsHelper.Track(TrackProgressEnum.BindComponent, "Bind Components.");
        }

        protected override void StartComponents()
        {
            LaunchAnalyticsHelper.Track(TrackProgressEnum.StartComponent, "Start Components.");
            base.StartComponents();
        }
 
        protected override void StartApplication()
        {
            SpriteAtlasManager.atlasRequested += OnSpriteAtlasRequested;
            base.StartApplication();
        }

        protected override void ApplicationStarted()
        {
            base.ApplicationStarted();
            Camera.main.AddCameraStack();
            Log.Info("Enter Game");
            var isDebug = !gameSetting.isRelease;
            if (isDebug)
            {
                SRDebug.Init();
                LFrameworkAspect.Instance.Get<DebuggerComponent>().RegisterDebuggerWindow();
            }
        }

        private void InitializeAnalyticsWhenFirstAwake()
        {
            var isOpen = true;
#if UNITY_EDITOR
            isOpen = gameSetting.enableAnalytics;
#endif
            var isOpenLog = false;
#if UNITY_EDITOR
            isOpenLog = gameSetting.enableAnalyticsLog;
#endif

            var go = new GameObject("ThinkingAnalytics", typeof(TDAnalytics));
            Object.DontDestroyOnLoad(go);
            string appId = "78123ccd4b8b4975a5e17b0fc48543d0";
            string serverUrl = "https://tga.gorillajoy.com";
            var token = new TDConfig(appId, serverUrl)
            {
                mode = !isOpen ? TDMode.DebugOnly : TDMode.Normal,
                timeZone = TDTimeZone.UTC
            };
            TDAnalytics.Init(token);
            TDAnalytics.EnableAutoTrack(TDAutoTrackEventType.All);

